import './App.css'
import { useState } from 'react'

function BetSubmitter() {


    const [name, setName] = useState('')
    const [email, setEmail] = useState('')

    const [time, setTime] = useState('')
    const [sport, setSport] = useState('')
    const [odds, setOdds] = useState('')
    const [stake, setStake] = useState('')

    // Step 3: Event handler for form submission
    const handleSubmit = (e) => {
        e.preventDefault(); // Prevent the default form submission behavior

        // Step 4: Handle the form submission, e.g., send data to an API
        // For this example, we'll just log the data to the console
        alert(`Name: ${name}, Email: ${email}, Stake: ${stake}`);
    };

    function inputGen(name, val, setVal, type) {

        return (
            <div>
                <label>{name}: </label>
                <input
                    type={type}
                    id={name.toLowerCase()}
                    value={val}
                    onChange={(e) => setVal(e.target.value)}
                />
            </div>
        )
    }

    return (
        <div>
            <h2>Submit Section</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name:</label>
                    <input
                        type="text"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>
                <div>
                    <label>Email:</label>
                    <input
                        type="text"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </div>
                {inputGen('Stake', stake, setStake, "number")}
                <button type="submit">Submit</button>
            </form>
        </div>
    )

}

export default BetSubmitter;
